import React, {useState, useEffect} from 'react'
import './SimpsonQuotes.css'

const SimpsonQuotes = () => {
    const[citation, setCitation] = useState('dalu');
    const[img, setImg] = useState('');

    useEffect((ev) => {
        fetch("https://thesimpsonsquoteapi.glitch.me/quotes")
        .then(res => res.json())
        .then(res => {
            setCitation(res.quotes)
            setImg(res.image)
        })
        console.log({citation})
    }
    )

    return (
        <div>
            <button id="buttonGenerate" onClick={useEffect}>press here</button>
            <h1>{citation}</h1>
            <img src={img}/>
        </div>
    )
}

export default SimpsonQuotes;
