import React, {useState, useEffect} from 'react'
import './SimpsonQuotes.css'

const SimpsonQuotes = () => {
    const[citation, setCitation] = useState();
    const[img, setImg] = useState();

    const recup = () => {
        fetch("https://thesimpsonsquoteapi.glitch.me/quotes")
        .then(res => res.json())
        .then(res => {
            setCitation(res.quotes)
            setImg(res.image)
            console.log({res})
        })

    }

    const exit = () => {
        citation.map(p => {
            return p
        })
    }


    return (
        <div>
            <button id="buttonGenerate" onClick={recup, exi}>press here</button>
            <h1>{citation}</h1>
            <img src={img}/>
        </div>
    )
}

export default SimpsonQuotes;
