import React, {useState, useEffect} from 'react'
import './SimpsonQuotes.'

const SimpsonQuotes = () => {
    const[citation, setCitation] = useState();
    const[img, setImg] = useState();

    useEffect(() => {
        fetch("https://thesimpsonsquoteapi.glitch.me/quotes")
        .then(res => res.json())
        .then(res => {
            setCitation(res.quotes)
            setImg(res.image)
        })
    }
    )

    const presseButton = (event) => {
        setCitation(event.target.citation)
        setImg(event.target.img)
    }

    return (
        <div>
            <button id="buttonGenerate" onClick={presseButton}/>
            <h1>{citation}</h1>
            <img src={img}/>
        </div>
    )
}

export default SimpsonQuotes;
